import { createFeatureSelector, createSelector } from '@ngrx/store';
import * as fromUserData from './userData/state/userData.reducer';
import * as fromShared from './state/shared-reducer';
export const sharedReducers = {
  userData: fromUserData.reducer,
  sharedState: fromShared.sharedReducer
};
export const selectSharedModuleState = createFeatureSelector('shared');
export const selectUserData = createSelector(selectSharedModuleState, state => state.userData);
export const selectSharedState = createSelector(selectSharedModuleState, state => state.sharedState);
