import * as fromUser from './state/user.reducer';
import * as fromSearchBar from './searchbar/state/searchbar.reducer';
import * as fromSearchBarResultsData from './searchbar-results/state/searchbar-results.reducer';
import { createFeatureSelector, createSelector } from '@ngrx/store';
export const userReducers = {
  user: fromUser.userReducer,
  searchBar: fromSearchBar.searchBarReducer,
  searchBarResultsData: fromSearchBarResultsData.reducer
};
export const selectUserModuleState = createFeatureSelector('userState');
export const selectUserState = createSelector(selectUserModuleState, state => state.user);
export const selectSearchBarState = createSelector(selectUserModuleState, state => state.searchBar);
export const selectSearchBarResultsState = createSelector(selectUserModuleState, state => state.searchBarResultsData);
