import { createSelector } from '@ngrx/store';
import * as fromUserRoot from '../index';
export const getUserId = createSelector(fromUserRoot.selectUserState, state => state.id);
export const getUsername = createSelector(fromUserRoot.selectUserState, state => state.username);
export const getUserEmailAddress = createSelector(fromUserRoot.selectUserState, state => state.email);
export const getProfilePicUrl = createSelector(fromUserRoot.selectUserState, state => state.userPicUrl);
export const getCurrentUserInfo = createSelector(fromUserRoot.selectUserState, state => {
  return {
    username: state.username,
    email: state.email,
    id: state.id,
    userPicUrl: state.userPicUrl
  };
});
